    <h1>Une idée, un projet ? Contactez-moi !</h1>

    <form action="src/app/controlFormContact.php" method="POST" id="form-contact" class="flex-col" novalidate>
        <div class="container-disable"></div>
        <div class="form-customer-container">
            <div>
                <label for="name">Prénom:</label>
                <input type="text" name="name" id="name" class="input-disable" required>
            </div>
            <div>
                <label for="email">Mail:</label>
                <input type="email" name="email" id="email" class="input-disable" required>
            </div>
            <div>
                <label for="phone">Téléphone:</label>
                <input type="text" name="phone" id="phone" class="input-disable" required>
            </div>
        </div>

        <div class="form-message-container flex-col">
            <label for="message" class="label">Message:</label>
            <textarea id="message" class="input-disable" name="message"></textarea>
        </div>

        <div class="response-form flex-row font-courrier-prime"></div>
        
        <input type="submit" value="Envoyer" class="submit-form-contact">

    </form>

